mapper.select:
SELECT * FROM customer 

		hotel_seq=#{hotel_seq}
		AND cust_name LIKE #{searchValue}
		AND cust_phone LIKE #{searchValue}
		AND cust_email LIKE #{searchValue}

				AND cust_name LIKE #{searchValue} 
				OR hotel_seq=#{hotel_seq} AND cust_phone LIKE #{searchValue} 
				OR hotel_seq=#{hotel_seq} AND cust_email LIKE #{searchValue}

mapper.update:
UPDATE customer 
	SET cust_name=#{cust_name, jdbcType=VARCHAR}, cust_pw=#{cust_pw, jdbcType=VARCHAR}, cust_phone=#{cust_phone, jdbcType=VARCHAR}, cust_email=#{cust_email, jdbcType=VARCHAR}, cust_rating=#{cust_rating, jdbcType=VARCHAR}
		WHERE cust_seq=#{cust_seq}

mapper.select:
SELECT * FROM (
	SELECT 
			p.pd_seq, p.hotel_seq, p.reserv_seq, p.cust_seq, p.personnel, p.add_date, p.want_date, p.start_address, 
			p.start_point_width, p.start_point_height, p.end_address, p.end_point_width, p.end_point_height, p.complete_check,
			c.cust_name, c.cust_pw, c.cust_phone, c.cust_email, c.cust_rating, c.signup_date
		FROM pickdrop p LEFT JOIN customer c ON p.cust_seq = c.cust_seq

			p.hotel_seq=#{hotel_seq}
			AND c.cust_name LIKE #{searchValue}
			AND c.cust_phone LIKE #{searchValue}
			AND c.cust_email LIKE #{searchValue}

					AND c.cust_name LIKE #{searchValue}
					OR p.hotel_seq=#{hotel_seq} AND c.cust_phone LIKE #{searchValue}
					OR p.hotel_seq=#{hotel_seq} AND c.cust_email LIKE #{searchValue}


		) a WHERE a.complete_check=#{completeTypeBoolean}

SELECT * 
		FROM reservation r LEFT JOIN customer c ON r.cust_seq = c.cust_seq 


			c.cust_name LIKE #{searchValue}


			c.cust_phone LIKE #{searchValue}


			c.cust_email LIKE #{searchValue}


			AND r.hotel_seq=#{hotel_seq} AND r.state ='예약완료'
